            countryStateCity.push({ result: customerAddresses, csc: obj })
           return responseHandler(res, 200, "Success", countryStateCity)  
        }     
    }
    catch (e) {
        console.log("Error :=>", e)
        return responseHandler(res, 500, e)
    }
}

